import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {ILocation} from '../../models/location.interface';

@Component({
  selector: 'app-location-info',
  templateUrl: './location-info.component.html',
  styleUrls: ['./location-info.component.scss']
})
export class LocationInfoComponent implements OnInit {
  @Input() location!: ILocation;
  @Output() closeInfo: EventEmitter<boolean> = new EventEmitter<boolean>();
  imageURL?: string;

  ngOnInit(): void {
    const reader = new FileReader();
    reader.onload = () => {
      this.imageURL = reader.result as string;
    }
    reader.readAsDataURL(this.location.imgBlob);
  }

  onClose(): void {
    this.closeInfo.emit(true);
  }

}
<div class="card-wrapper">
  <div class="card-header">Location Details</div>
  <div class="card-body">
    <img class="img-wrapper" [src]="imageURL" alt="logo">
    <section class="info-wrapper">
      <div>
        <p>Name:</p>
        <strong>{{location.name}}</strong>
      </div>
      <div>
        <p>Type:</p>
        <strong>{{location.type}}</strong>
      </div>

    </section>
  </div>
  <div class="button-wrapper">
    <button class="btn btn-close" (click)="onClose()">Close</button>
    <button class="btn btn-save" [routerLink]="['/location/edit/', location.id]">Edit</button>
  </div>

</div>
